import './App.css';
import Header from './component/Header';
import Footer from './component/Footer';
import { useState } from 'react';
import BlogPost from './component/BlogPost';
import Filter from './component/Filter';
import { v4 as uuidv4 } from 'uuid';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSave, faClose } from '@fortawesome/free-solid-svg-icons'

function App() {

 const savebtn = <FontAwesomeIcon icon={faSave} />
const closebtn = <FontAwesomeIcon icon={faClose} />

  const [post, setPost] = useState([]);
  const [allData, setAllData] = useState([]);
  const [edittext, setEditText] = useState(false)

  const [title, setTitle] = useState('')
  const [author, setAuthor] = useState('')
  const [date, setDate] = useState('')
  const [content, setContent] = useState('')



  const getTitle = (event) => {
    const title = event.target.value;
    setTitle(title)
  }

  const getAuthor = (event) => {
    const author = event.target.value;
    setAuthor(author)
  }

  const getDate = (event) => {
    const date = event.target.value;
    setDate(date)
  }

  const getContent = (event) => {
    const content = event.target.value;
    setContent(content)
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    const newPost = {
      id: uuidv4(),
      title: title,
      author: author,
      date: date,
      content: content,
    }
    setPost([...post, newPost]);
    setAllData([...allData, newPost])  //for filter purposes

    // clear form inputs
    setTitle('');
    setAuthor('');
    setDate('');
    setContent('');

  }

const [editTitle, setEditTitle] = useState('')

   const handleEditTitle = (event) => {
    const title = event.target.value;
    setEditTitle(title)
    console.log(editTitle)
  }

console.log(edittext)
  return (
    <div className="App">

     <Header />


                <div className='FormContainer'>
                 <form onSubmit={handleSubmit}>
                      <div className='title-container'>
                          <label>Title</label>
                          <input required value={title} onChange={getTitle}></input>
                          <label>Author</label>
                          <input required value={author} onChange={getAuthor}></input>
                          <label>Date</label>
                          <input  required type="date" value={date} onChange={getDate}></input>
                          <label>Content</label>
                          <textarea required  value={content} onChange={getContent}></textarea>
                          <button type="submit">Submit</button>
                      </div>
                  </form>

                  </div>

               <Filter />

                   <div className='displayBlog'>
                   <BlogPost post={post} allData={allData} setPost={setPost} setAllData={setAllData}  setEditText={setEditText} />         
                   </div>

                   <div>
                   {edittext.length >0  && (
                      <>
                      {edittext.map(el =>{
                        return (
                        <div key={el.id}>
                          <div className='displayEditField'>
                            <form onSubmit={handleSubmit}>
                               <h1>Edit Content</h1>  
                               <div className='inputHolder'>
                               <input className='editText'value={el.title} onChange={handleEditTitle} ></input>
                               <input className='editText' value={el.author}></input>
                               <input className='editText' type='date' value={el.date}></input>
                               <textarea className='areatext' value={el.content}></textarea>
                               </div>
                               <div className='btnHolder'>
                               <button className='savebtn'>{savebtn}</button>
                               <button className='closebtn'>{closebtn}</button>
                               </div>
                               </form>
                          </div>
                        </div>
                        )
                      })}
                      </>
                   )}
                   </div>

     <Footer />
    </div>
  );
}

export default App;


