import React from 'react'
import './BlogPost.css';
import Filter from './Filter';
const BlogPost = ({setPost}) => {
    

  return (
    
    
       <div className='container'>
            <Filter />
                    {setPost.map(el => (
                    <div className='DisplayChildElement' key={el.id}>
                      <h1>{el.title}</h1>
                      <div className='authorAndDate'>
                          <p> {el.author}</p>
                          <p>{el.date}</p>
                      </div>
                        <div className='contentText'>
                          <p>{el.content}</p>
                      </div>
                    </div>
                ))}

       </div>
    
  )
}

export default BlogPost
