import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit, faTrashCan } from '@fortawesome/free-solid-svg-icons'

import React from 'react'
import './BlogPost.css';

const BlogPost = ({ post, allData, setPost, setAllData, edittext, setEditText }) => {
    
const editbtn = <FontAwesomeIcon icon={faEdit} />
const deletebtn = <FontAwesomeIcon icon={faTrashCan} />


  const handleDelete = (id) => {
  const updatedPost = post.filter((el) => el.id !== id);
  setPost(updatedPost);
  const updatedData = allData.filter((el) => el.id !== id);
  setAllData(updatedData);
  
  console.log(updatedData);
  console.log(updatedPost);
};


  return (
    
       <div className='container'>
          
                    {post.map(el => (
                    <div className='DisplayChildElement' id='close' key={el.id}>
                      <h1>{el.title}</h1>
                      <div className='authorAndDate'>
                          <p> {el.author}</p>
                          <p>{el.date}</p>
                      </div>
                        <div className='contentText'>
                          <p>{el.content}</p>
                      </div>
                      <div className='delAndedit'>
                         <button className='delbtn' onClick={()=> handleDelete(el.id)}>{deletebtn}</button>
                        
                         <button className='editbtn' >{editbtn}
                         
                         </button>
                        
                        
                       </div>
                    </div>
                ))}

       </div>
    
  )
}

export default BlogPost
