import React from 'react'
import './BlogPost.css';

const BlogPost = ({setPost}) => {
    const data =[
                        {
                            id: 1,
                            title:'Lord of the ring',
                            author:'Jehros Rina',
                            data: 'January 20, 2023',
                            content:' Lorem Epselum content',
                        },
                           {
                            id: 2,
                            title:'harry Potter',
                            author:'Jehros Rina',
                            data: 'January 10, 2001',
                            content:' Lorem Epselum content',
                        },
                        
                ]


  return (
    
    
       <div className='container'>
                    {setPost.map(el => (
                    <div className='DisplayChildElement' key={el.id}>
                      <h1>{el.title}</h1>
                      <div className='authorAndDate'>
                          <p>By: {el.author}</p>
                          <p>{el.date}</p>
                      </div>
                        <div className='contentText'>
                          <p>{el.content}</p>
                      </div>
                    </div>
                ))}

       </div>
    
  )
}

export default BlogPost
