import React from 'react'
import './BlogPost.css';
const BlogPost = ({setTitle,setAuthor,setDate, setContent}) => {

const getTitle =(event) =>{
const title = event.target.value;
setTitle(title)

}
const getAuthor =(event) =>{
    const author = event.target.value;
    setAuthor(author)
}

const getDate =(event) =>{
    const getdata = event.target.value;
    setDate(getdata)
}

const getContent =(event) =>{
    const content = event.target.value;
    setContent(content)
}


  return (
    <div className='FormContainer'>
     <form>
        <div className='title-container'>
            <label>Title</label>
            <input onChange={getTitle}></input>
            <label>Author</label>
            <input onChange={getAuthor}></input>
            <label>Date</label>
            <input onChange={getDate}></input>
            <label>Content</label>
            <textarea onChange={getContent}></textarea>
            <button>Submit</button>
        </div>
     </form>

    </div>
  )
}

export default BlogPost
