import './App.css';
import Header from './component/Header';
import Footer from './component/Footer';
import { useState } from 'react';
import BlogPost from './component/BlogPost';
import Filter from './component/Filter';
import { v4 as uuidv4 } from 'uuid';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSave, faClose } from '@fortawesome/free-solid-svg-icons'

function App() {

 const savebtn = <FontAwesomeIcon icon={faSave} />
const closebtn = <FontAwesomeIcon icon={faClose} />

  const [post, setPost] = useState([]);
  const [allData, setAllData] = useState([]);
  const [edittext, setEditText] = useState([])
  const [saveChanges, setSaveChanges] = useState([]);


  const [title, setTitle] = useState('')
  const [author, setAuthor] = useState('')
  const [date, setDate] = useState('')
  const [content, setContent] = useState('')

 console.log(edittext)

  const getTitle = (event) => {
    const title = event.target.value;
    setTitle(title)
  }

  const getAuthor = (event) => {
    const author = event.target.value;
    setAuthor(author)
  }

  const getDate = (event) => {
    const date = event.target.value;
    setDate(date)
  }

  const getContent = (event) => {
    const content = event.target.value;
    setContent(content)
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    const newPost = {
      id: uuidv4(),
      title: title,
      author: author,
      date: date,
      content: content,
    }
    setPost([...post, newPost]);
    setAllData([...allData, newPost])  //for filter purposes

    // clear form inputs
    setTitle('');
    setAuthor('');
    setDate('');
    setContent('');

  }



const handleCloseEditField =(id) =>{
 const CancelEdit = edittext.filter((el) => el.id !== id);
 setEditText(CancelEdit)


}

const [editTitle, setEditTitle] =useState('')
const [editAuthor, setEditAuthor] =useState('')
const [editDate,  setEditDate] =useState('')
const [editContent,setEditContent] =useState('')



const handleEditTitle =(event) =>{
 const title = event.target.value;
 setEditTitle(title)
}
const handleEditAuthor=(event) =>{
 const author = event.target.value;
 setEditAuthor(author)
}
const handleEditDate =(event) =>{
 const getdate = event.target.value;
 setEditDate(getdate)
}

const handleEditContent =(event) =>{
 const content = event.target.value;
 setEditContent(content)
}



const handleSave =(id) =>{
if(edittext.filter((el) => el.id === id)){
 const updateData ={

      id: id,
      title: editTitle,
      author: editAuthor,
      date: editDate,
      content: editContent,
 }
setSaveChanges(updateData)
setEditText(updateData)

 }
}




  return (
    <div className="App">

     <Header />


                <div className='FormContainer'>
                 <form onSubmit={handleSubmit}>
                      <div className='title-container'>
                          <label>Title</label>
                          <input required value={title} onChange={getTitle}></input>
                          <label>Author</label>
                          <input required value={author} onChange={getAuthor}></input>
                          <label>Date</label>
                          <input  required type="date" value={date} onChange={getDate}></input>
                          <label>Content</label>
                          <textarea required  value={content} onChange={getContent}></textarea>
                          <button type="submit">Submit</button>
                      </div>
                  </form>

                  </div>

               <Filter />

                   <div className='displayBlog'>
                   <BlogPost post={post} allData={allData} setPost={setPost} setAllData={setAllData}  setEditText={setEditText} />         
                   </div>

                   <div>
                   {edittext.length >0  && (
                      <>
                      {edittext.map(el =>{
                        return (
                        <div key={el.id}>
                          <div className='displayEditField'>

                               <h1>Edit Content</h1>  
                               <div className='inputHolder'>
                               <input className='editText' onChange={handleEditTitle}></input>
                               <input className='editText' onChange={handleEditAuthor}></input>
                               <input className='editText' onChange={handleEditDate} type='date'></input>
                               <textarea className='areatext' onChange={handleEditContent}></textarea>
                               </div>
                               <div className='btnHolder'>
                               <button className='savebtn'onClick={()=> handleSave(el.id)}>{savebtn}</button>
                               <button className='closebtn'onClick={()=> handleCloseEditField(el.id)}>{closebtn}</button>
                               </div>

                          </div>
                        </div>
                        )
                      })}
                      </>
                   )}
                   </div>

     <Footer />
    </div>
  );
}

export default App;
