import './App.css';
import Header from './component/Header';
import Footer from './component/Footer';
import { useState } from 'react';
import BlogPost from './component/BlogPost';
import Filter from './component/Filter';
import { v4 as uuidv4 } from 'uuid';
function App() {

  const [post, setPost] = useState([]);
  const [allData, setAllData] = useState([]);

  const [title, setTitle] = useState('')
  const [author, setAuthor] = useState('')
  const [date, setDate] = useState('')
  const [content, setContent] = useState('')
  const [filtered, setFilter] = useState('')
    const [filteredData, setFilteredData] = useState([]);

  const getTitle = (event) => {
    const title = event.target.value;
    setTitle(title)
  }

  const getAuthor = (event) => {
    const author = event.target.value;
    setAuthor(author)
  }

  const getDate = (event) => {
    const date = event.target.value;
    setDate(date)
  }

  const getContent = (event) => {
    const content = event.target.value;
    setContent(content)
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    const newPost = {
      id: uuidv4(),
      title: title,
      author: author,
      date: date,
      content: content,
    }
    setPost([...post, newPost]);
    setAllData([...allData, newPost])  //for filter purposes
    setAllData([...allData, newPost]);
    // clear form inputs
    setTitle('');
    setAuthor('');
    setDate('');
    setContent('');

  }

  useEffect(() => {
    if (filtered === "") {
      setFilteredData(allData);
    } else {
      const filteredPosts = allData.filter((post) => post.title.includes(filtered));
      setFilteredData(filteredPosts);
    }
  }, [filtered, allData]);


  console.log(allData)
  console.log(filtered)
  return (
    <div className="App">

     <Header />


                <div className='FormContainer'>
                 <form onSubmit={handleSubmit}>
                      <div className='title-container'>
                          <label>Title</label>
                          <input required value={title} onChange={getTitle}></input>
                          <label>Author</label>
                          <input required value={author} onChange={getAuthor}></input>
                          <label>Date</label>
                          <input  required type="date" value={date} onChange={getDate}></input>
                          <label>Content</label>
                          <textarea required  value={content} onChange={getContent}></textarea>
                          <button type="submit">Submit</button>
                      </div>
                  </form>

                  </div>
               <Filter setFilter={setFilter}/>
                   <div className='displayBlog'>
                   <BlogPost setPost={post}  allData={allData}  setAllData={setAllData} />          
                   </div>

     {/* <Filter setFilter={setFilter}/>
      {allData.length >0 && (
        <>
         {allData.map(el =>{
          return (
            <div key={el.id}>
            <p>{el.tile}</p>
                <p>{el.author}</p>
                    <p>{el.date}</p>
                     <p>{el.content}</p>
            </div>
          )
         })}
        </>
      )}
      */}
     <Footer />
    </div>
  );
}

export default App;


