import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit, faTrashCan } from '@fortawesome/free-solid-svg-icons'
import { useState } from 'react';
import React from 'react'
import './BlogPost.css';

const BlogPost = ({ post, allData, setPost, setAllData, edittext, setEditText }) => {
    
const editbtn = <FontAwesomeIcon icon={faEdit} />
const deletebtn = <FontAwesomeIcon icon={faTrashCan} />


  const handleDelete = (id) => {
  const updatedPost = post.filter((el) => el.id !== id);
  setPost(updatedPost);
  const updatedData = allData.filter((el) => el.id !== id);
  setAllData(updatedData);
  
  console.log(updatedData);
  console.log(updatedPost);
};


  const [title, setTitle] = useState('')
  const [author, setAuthor] = useState('')
  const [date, setDate] = useState('')
  const [content, setContent] = useState('')
 

 const getTitle = (event) => {
    const title = event.target.value;
    setTitle(title)
  }

  const getAuthor = (event) => {
    const author = event.target.value;
    setAuthor(author)
  }

  const getDate = (event) => {
    const date = event.target.value;
    setDate(date)
  }

  const getContent = (event) => {
    const content = event.target.value;
    setContent(content)
  }


