  }
 }
} 


  return (
    
       <div className='container'>
          
                    {post.map(el => (
                    <div className='DisplayChildElement' id='close' key={el.id}>
                      <h1>{el.title}</h1>
                      <div className='authorAndDate'>
                          <p> {el.author}</p>
                          <p>{el.date}</p>
                      </div>
                        <div className='contentText'>
                          <p>{el.content}</p>
                      </div>
                      <div className='delAndedit'>
                         <button className='delbtn' onClick={()=> handleDelete(el.id)}>{deletebtn}</button>
                         <label>Title</label>
                          <input required value={title} onChange={getTitle}></input>
                          <label>Author</label>
                          <input required value={author} onChange={getAuthor}></input>
                          <label>Date</label>
                          <input  required type="date" value={date} onChange={getDate}></input>
                          <label>Content</label>
                          <textarea required  value={content} onChange={getContent}></textarea>
                          <button type="submit">Submit</button>
                         <button className='editbtn'onClick={()=> handleEdit(el.id)} >{editbtn}
                         
                         </button>
                        
                        
                       </div>
                    </div>
                ))}

       </div>
    
  )
}

export default BlogPost
