import './App.css';
import Header from './component/Header';
import Footer from './component/Footer';
import { useState } from 'react';
import BlogPost from './component/BlogPost';

function App() {

  const [post, setPost] = useState([]);
  const [blogData, setblogData] = useState([]);

  const [title, setTitle] = useState('')
  const [author, setAuthor] = useState('')
  const [date, setDate] = useState('')
  const [content, setContent] = useState('')

  const getTitle = (event) => {
    const title = event.target.value;
    setTitle(title)
  }

  const getAuthor = (event) => {
    const author = event.target.value;
    setAuthor(author)
  }

  const getDate = (event) => {
    const date = event.target.value;
    setDate(date)
  }

  const getContent = (event) => {
    const content = event.target.value;
    setContent(content)
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    const newPost = {
      id: post.length + 1,
      title: title,
      author: author,
      date: date,
      content: content,
    }
    setPost([...post, newPost]);
    // clear form inputs
    setTitle('');
    setAuthor('');
    setDate('');
    setContent('');

  }
console.log(post)
  return (
    <div className="App">
     <Header />
                <div className='FormContainer'>
                 <form onSubmit={handleSubmit}>
                      <div className='title-container'>
                          <label>Title</label>
                          <input required value={title} onChange={getTitle}></input>
                          <label>Author</label>
                          <input required value={author} onChange={getAuthor}></input>
                          <label>Date</label>
                          <input  required type="date" value={date} onChange={getDate}></input>
                          <label>Content</label>
                          <textarea required  value={content} onChange={getContent}></textarea>
                          <button type="submit">Submit</button>
                      </div>
                  </form>

                  </div>

              <BlogPost setPost={post} data={blogData}  />




     <Footer />
    </div>
  );
}

export default App;
