import React, { useState } from 'react'
import './Filter.css';

const Filter = ({ setStatus, status, filtered={filtered}, setFiltered={setFiltered}, post, allData, setPost, setAllData, setEditText}) => {

const [erroMessage, setErrormesasge] =useState('')
        
        
const handleStatusChange = (event) => {
  const getValue = event.target.value;
  setStatus(getValue);

  if (allData.length > 0) {
    const result = allData.filter((el) => {
      // Check if the date matches the selected value
      return el.date === status;
    });

    setFiltered(result);
    console.log('This is the alldata')
    console.log(allData)

    console.log('This is the result')
    console.log(result)
    
    console.log('This is filtered taht pass by teh setfiltered function')
    console.log(filtered)

    if (result.length === 0) {
       setFiltered([]);
       setErrormesasge('No Blog Found');
    } else {
      setErrormesasge('');
    }
  } else {
    setErrormesasge('No Blog Posted Found');
  }
};


     
 
  return (
<>
    <div className='containerSelect'>
        <select className='container' onChange={handleStatusChange}>
          
           <option value={'FilteredDate'}>Filtered By Date</option>
           <option value='2023-03-01'>2023-03-01</option>
           <option value='2023-01-01'>2023-01-01</option>
        </select>  
    </div>



    <div className='DisplayResult'>
       <h1>{erroMessage}</h1>
       <div >
   
      <>
      {filtered.map(el =>{
        return (
          <div  key={el.id}>
            <h1>LIST OF BLOGS FILTERED BY DATE</h1>
         
              
              <div className='displayfilter'>
              <p>{el.title}</p>
               <p>{el.author}</p>
               <p>{el.date}</p>
               <p>{el.content}</p>
               </div>
            </div>
        
        )
      })}
      </>

   </div>
    </div>

    

   </> 
  )
}

export default Filter
