import './App.css';
import Header from './component/Header';
import Footer from './component/Footer';
import { useState } from 'react';
import BlogPost from './component/BlogPost';

function App() {

  const [post, setPost] = useState([]);
  const [title, setTitle] = useState('')
  const [author, setAuthor] = useState('')
  const [date, setDate] = useState('')
  const [content, setContent] = useState('')


  const data =[
                        {
                            id: 1,
                            title:'Lord of the ring',
                            author:'Jehros Rina',
                            data: 'January 20, 2023',
                            content:' Lorem Epselum content',
                        },
                           {
                            id: 2,
                            title:'harry Potter',
                            author:'Jehros Rina',
                            data: 'January 10, 2001',
                            content:' Lorem Epselum content',
                        },

                ]


  const getTitle = (event) => {
    const title = event.target.value;
    setTitle(title)
  }

  const getAuthor = (event) => {
    const author = event.target.value;
    setAuthor(author)
  }

  const getDate = (event) => {
    const date = event.target.value;
    setDate(date)
  }

  const getContent = (event) => {
    const content = event.target.value;
    setContent(content)
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    const newPost = {
      id: data.length + 1,
      title: title,
      author: author,
      date: date,
      content: content,
    }
    setPost([...post, newPost]);
    // clear form inputs
    setTitle('');
    setAuthor('');
    setDate('');
    setContent('');
  }

  return (
    <div className="App">
     <Header />
                <div className='FormContainer'>
                 <form onSubmit={handleSubmit}>
                      <div className='title-container'>
                          <label>Title</label>
                          <input required value={title} onChange={getTitle}></input>
                          <label>Author</label>
                          <input required value={author} onChange={getAuthor}></input>
                          <label>Date</label>
                          <input  required type="date" value={date} onChange={getDate}></input>
                          <label>Content</label>
                          <textarea required  value={content} onChange={getContent}></textarea>
                          <button type="submit">Submit</button>
                      </div>
                  </form>

                  </div>
                  <div className='container'>
                     {post.map(el => (
                    <div className='Display' key={el.id}>
                      <p>{el.title}</p>
                      <p>By: {el.author}</p>
                      <p>{el.date}</p>
                      <p>{el.content}</p>
                    </div>
                  ))}
                  <BlogPost data={setPost} />
                  </div>



     <Footer />
    </div>
  );
}

export default App;
