import React, { useState } from 'react'
import './Filter.css';
const Filter = ({ setStatus, status, post, allData, setPost, setAllData, setEditText}) => {

   const [FilteredValue, SetFilterValue] =useState([])

        const handleStatusChange = (event) => {
        const getValue = event.target.value;
        console.log('This is the post value')
        console.log(post)
        console.log('this is all Data value')
        console.log(allData)
        console.log('This is the status value')
        setStatus(getValue);
      
        
        // {post.length >0? (
        //    setErrorMessage('')
        // ):
        // (
          
        //    setErrorMessage('No Blog Found')
        // )
        // }

        if(post.length > 0){
          const result = post.map(el =>{
            if(el.date === status){
              console.log(result)
              SetFilterValue(result)
            }
          })
        }else{
          
          SetFilterValue(' blog  not Found')
        }

} 

 
  return (
<>
    <div className='containerSelect'>
        <select className='container' onChange={handleStatusChange}>
          
           <option>Filtered By Date</option>
           <option value='January 1, 2023'>January 1, 2023</option>
           
           <option value='March 3, 2023'>March 3, 2023</option>
        </select>  
    </div>



    <div className='DisplayResult'>
        {post.length >0?(
           <div>Blog Found</div>
        ):
        (
           <div>Blog Not Found</div>
        )
        }
    </div>

   </> 
  )
}

export default Filter
